export default {
  name: "SplineChart",
  props: ['data'],
  data() {
    const revenueByMonth = new Array(12).fill(0);
    this.data.map(item => {
      if (item._id && item.totalRevenue) {
        revenueByMonth[item._id - 1] = item.totalRevenue / 1000000;
      }
    });
    return {
      chartOptions: {
        chart: {
          id: 'line_chart'
        },
        xaxis: {
          categories: Array.from({
            length: 12
          }, (v, i) => `${i + 1}`)
        },
        title: {
          text: "Doanh thu (triệu VND)",
          align: "center",
          margin: 20,
          style: {
            fontSize: "18px",
            fontWeight: "bold",
            color: "#333"
          }
        }
      },
      series: [{
        name: 'Doanh thu',
        data: []
      }]
    };
  },
  watch: {
    // Watcher cho prop 'data'
    data: {
      immediate: true,
      // Chạy ngay khi component được tạo
      deep: true,
      // Theo dõi sâu các thay đổi bên trong object
      handler(newVal) {
        if (newVal) {
          this.series[0].data = newVal.map(item => {
            if (item._id && item.totalRevenue) {
              return item.totalRevenue / 1000000;
            }
            return 0;
          });
        }
      }
    }
  }
};
