export default {
  name: "SplineChart",
  props: ['data'],
  data: function () {
    return {
      chartOptions: {
        chart: {
          id: 'line_chart'
        },
        xaxis: {
          categories: this.data.map(item => item.map(i => i._id))
        },
        title: {
          text: "Doanh thu" + this.data.map(item => item.year),
          align: "center",
          margin: 20,
          style: {
            fontSize: "18px",
            fontWeight: "bold",
            color: "#333"
          }
        }
      },
      series: [{
        name: 'series-1',
        data: this.data.filter(item => item.map(i => i.totalRevenue))
      }]
    };
  }
};
