export default {
  name: "SplineChart",
  props: ['data'],
  data: function () {
    const categories = Array.from({
      length: 12
    }, (v, i) => `Tháng ${i + 1}`);
    const revenueByMonth = new Array(12).fill(0);
    this.data.forEach(item => {
      if (item.month && item.totalRevenue) {
        revenueByMonth[item.month - 1] = item.totalRevenue;
      }
    });
    return {
      chartOptions: {
        chart: {
          id: 'line_chart'
        },
        xaxis: {
          categories: this.data.map(item => item._id)
        },
        title: {
          text: "Doanh thu",
          align: "center",
          margin: 20,
          style: {
            fontSize: "18px",
            fontWeight: "bold",
            color: "#333"
          }
        }
      },
      series: [{
        name: 'Doanh thu',
        data: this.data.map(item => item.totalRevenue)
      }]
    };
  }
};
