export default {
  name: "SplineChart",
  props: ['data'],
  data: function () {
    return {
      chartOptions: {
        chart: {
          id: 'line_chart'
        },
        xaxis: {
          categories: this.chartData.categories
        },
        title: {
          text: "Doanh thu (triệu VND)",
          align: "center",
          margin: 20,
          style: {
            fontSize: "18px",
            fontWeight: "bold",
            color: "#333"
          }
        }
      },
      series: [{
        name: 'Doanh thu',
        data: this.chartDatarevenueByMonth
      }]
    };
  },
  computed: {
    // Tạo computed property cho categories và revenueByMonth
    chartData() {
      const categories = Array.from({
        length: 12
      }, (v, i) => `${i + 1}`);
      const revenueByMonth = this.data.reduce((acc, item) => {
        if (item._id && item.totalRevenue) {
          acc[item._id - 1] = item.totalRevenue / 1000000;
        }
        return acc;
      }, new Array(12).fill(0));
      return {
        categories,
        revenueByMonth
      };
    }
  }
};
