import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const store=new Vuex.Store({
    state(){
        return{
            loading:false,
            error:false,
            user:JSON.parse(localStorage.getItem('user')) || null
        }
    },
    mutations:{
        LOGIN_START(state,user){
            state.loading=true,
            state.user=null,
            state.error=false
        },
        LOGIN_SUCCESS(state,user){
            state.isLoggerin=false,
            state.user=null,
            state.error=false
            localStorage.setItem('user',JSON.stringify(user))
        },
        SET_LOGOUT(state){
            state.isLoggerin=false,
            state.user=null,
            localStorage.removeItem('user');
        }
    },
    getters: {
        currentUser: state => {
          return state.user
        }
      }
})
export default store
