export default {
  name: "PieChart",
  props: ['data'],
  data() {
    const categories = Array.from({
      length: 12
    }, (v, i) => `Tháng ${i + 1}`);
    const revenueByMonth = new Array(12).fill(0);
    this.data.map(item => {
      if (item._id && item.totalRevenue) {
        revenueByMonth[item._id - 1] = item.totalRevenue / 1000000;
      }
    });
    return {
      series: revenueByMonth,
      chartOptions: {
        chart: {
          width: 380,
          type: 'pie',
          toolbar: {
            show: true,
            offsetX: 0,
            offsetY: 0,
            tools: {
              download: true,
              selection: true,
              zoom: true,
              zoomin: true,
              zoomout: true,
              pan: true,
              reset: true,
              customIcons: []
            },
            autoSelected: 'zoom'
          }
        },
        labels: categories,
        responsive: [{
          breakpoint: 480,
          options: {
            chart: {
              width: 200
            },
            legend: {
              position: 'bottom'
            }
          }
        }]
      }
    };
  }
};
