import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const store=new Vuex.Store({
    state(){
        return{
            isLoggerin:false,
            user:JSON.parse(localStorage.getItem('user')) || null
        }
    },
    mutations:{
        SET_LOGIN(state,user){
            state.isLoggerin=true,
            state.user=user,
            localStorage.setItem('user',JSON.stringify(user))
        },
        SET_LOGIN(state,user){
            state.isLoggerin=true,
            state.user=user,
            localStorage.setItem('user',JSON.stringify(user))
        },
    }
})
export default store
