import Vue from 'vue'
import VueRouter from 'vue-router'
import HomeView from '@/views/HomeView.vue'
import RegisterView from '@/views/RegisterView.vue'
import LoginView from '@/views/LoginView.vue'
import ProductDetailView from '@/views/ProductDetailView.vue'
import CategoryView from '@/views/CategoryView.vue'
import CartView from '@/views/CartView.vue'
import PaymentView from '@/views/PaymentView.vue'
import ContactView from '../views/ContactView.vue'
import NewView from '@/views/NewView.vue'
import SearchView from '@/views/SearchView.vue'
import NewDetailView from '@/views/NewDetailView.vue'
import ProfileView from '@/views/ProfileView.vue'
import Acount from '@/components/acount/Acount.vue'
import ChangePass from '@/components/changepass/ChangePass.vue'
import OrderView from '@/components/order/OrderView.vue'
import store from '../store/store'



Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: 'home',
    component: HomeView,
  },
  {
    path: '/register',
    name: 'register',
    component: RegisterView
  },
  {
    path: '/login',
    name: 'login',
    component:LoginView,

  },
  {
    path: '/ProductDetail/:id',
    name: 'productDetail',
    component: ProductDetailView,
  },
  {
    path: '/category/:NameCategory',
    name: 'category',
    component: CategoryView,
  },
  {
    path: '/cart',
    name: 'cart',
    component: CartView,
    meta: { requiresAuth: true },
  },
  {
    path: '/payment',
    name: 'payment',
    component: PaymentView,
  },
  {
    path: '/contact',
    name: 'contact',
    component: ContactView,
  },
  {
    path: '/news',
    name: 'news',
    component: NewView,
  },
  {
    path: '/news',
    name: 'newDetail',
    component: NewDetailView,
    meta: { requiresAuth: false },
  },
  {
    path: '/search',
    name: 'search',
    component: SearchView,
  },
  {
    path: '/profile',
    component: ProfileView,
    children: [
      {
        path: '',
        name: 'account', 
        component: Acount
      },
      {
        path: '/profile/changePass',
        name: 'changePass', 
        component: ChangePass
      },
      {
        path: '/profile/order',
        name: 'order', 
        component: OrderView
      },
    ]
  },
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

router.beforeEach(async (to, from, next) => {
  const { currentUser } = store.getters;
  // next-line: check if route ("to" object) needs authenticated
  if (to.matched.some((record) => record.meta.requiresAuth) && !currentUser) {
    next('/login');
  } else if (currentUser) {
    switch (to.name) {
      case 'login' || 'register' :
        next({ path: '/' });
        break;
      case 'home':
        next({ path: '/my-page' });
        break;
      default:
        next();
        break;
    }
  } else next();
});

export default router
