export default {
  name: "HomeView",
  data() {
    return {
      images: ["/slider/sl1.jpg", "/slider/sl2.jpg", "/slider/sl3.jpg"],
      currentIndex: 0,
      intervalId: null
    };
  },
  mounted() {
    this.startSlider();
  },
  methods: {
    nextSlide() {
      this.currentIndex = (this.currentIndex + 1) % this.images.length;
    },
    prevSlide() {
      this.currentIndex = (this.currentIndex - 1) % this.images.length;
    },
    startSlider() {
      this.intervalId = setInterval(() => {
        this.nextSlide();
      }, 2000);
    }
  }
};
